import React, { useState } from 'react'
import { Link, Outlet } from 'react-router-dom'

function TrainStation() {
  const [departureMode, setDepartureMode] = useState(true)

  const toggleMode = () => {
    setDepartureMode(!departureMode)
  };

  const modeLabel = departureMode ? 'Départs' : 'Arrivées'

  return (
    <div className="train-station">
      <div className="directions">
        <button onClick={toggleMode}>{modeLabel}</button>
        <Link to="../">Retour à la ville</Link>
      </div>
      <Outlet />
    </div>
  )
}

export default TrainStation
