import React, { useState } from 'react'
import { Link, Outlet, useNavigate, useParams } from 'react-router-dom'
import Arrivals from './Arrivals'
import Departures from './Departures'

function TrainStation() {
  const { codeStation } = useParams()
  const [departureMode, setDepartureMode] = useState(true)
  const navigate = useNavigate()

  const toggleMode = () => {
    setDepartureMode(!departureMode)
  }

  const modeLabel = departureMode ? 'Départs' : 'Arrivées'

  return (
    <div className="train-station">
      <div className="directions">
        <button onClick={toggleMode}>{modeLabel}</button>
        <Link to={`../${codeStation}`}>Retour à la ville</Link>
      </div>
      <Outlet />
    </div>
  )
}

export default TrainStation
